css:
.product-card section {
  border: 2px solid #ccc;
  padding: 10px
}
.info-bar {
background: #FFFFFF;
color: #FF0000
}
.rounded-icon .i-credit-card::before {
  background-image: url(/img/uploads/cadeador.png);
  background-size: 100% 100%;
  width: 30px;
  height: 30px;
  display: inline-block;
  content: ''
}
.rounded-icon .i-exchange::before {
  background-image: url(/img/uploads/phone4.png);
  background-size: 100% 100%;
  width: 30px;
  height: 30px;
  display: inline-block;
  content: ''
}
.header__submenu {
  background: #f8e2d5 !important;
}
.header__submenu * {
  color: #000000 !important
}
@media(min-width: 767px) {
  #logo {
    max-height: 225px !important;
  } 
} 
#user-button .i-user {
  color: #9d5386;
}
#cart-button .i-shopping-bag {
 color: #9d5386;
}
.header__submenu {
  max-width: none;
  min-width: 100%;
  left: 0%;
  gap: var(--spacer-1);
  margin-top: var(--spacer-2);
  grid-template-columns: repeat(auto-fit, minmax(15%, 1fr)); 
}
.breadcrumb * {
color: #fff000;
}
